import React from 'react'
import {Card} from 'antd'
import {
    FieldTimeOutlined
  } from '@ant-design/icons';
export const ResultCard = ({result, onSelectChange}) => {
    return(
        <Card className="card" 
        hoverable={true}
        title={result.title}
        key={result.id}
        size='small'
        cover={<img src={result.image} alt={""}/>} 
        onClick={() => onSelectChange(result.id)}
        >
            <div className="card-body">
                <div style={{display: 'block', height:'40px', overflow: 'hidden', textOverflow: 'ellipsis'}}>
                <h5 className="stat-text" style={{width: '100%', maxHeight: '40px',maxLines: 2, textOverflow:'ellipsis', overflow: 'hidden' }}>{result.summary}</h5>

                </div>
                <h5 className="stat-text" style={{}}>Calories: {parseInt(result.nutrition.nutrients[0].amount)}</h5>
                <div style={{display:'inline', width: '100%'}}>
                    <FieldTimeOutlined style={{float: 'right', fontSize:'1.2em'}}/>
                    <h5 className="stat-text" style={{float: 'right', marginRight: '8px'}}>{result.readyInMinutes} Minutes</h5>   
                </div>
            </div>
        </Card> 
    )
}
