import React from 'react'
import {Layout, Row, Col} from 'antd'
import SplashPage from './SplashPage.js'
import Directions from './Directions'
import Ingredients from './Ingredients'
import './css/AppContent.css'
import {Remarkable} from 'remarkable';
import {useTransition, animated} from 'react-spring'

export const AppContent = ({ID, recipe, useMetric, ingred, instructions}) => {
    const transitions = useTransition(ID < 0, null, {
        from: { position: 'absolute', opacity: 0 },
        enter: { opacity: 1 },
        leave: { opacity: 0 },
        })

    function createMarkup() {
        const md = new Remarkable();
        md.set({
            html: true,
            breaks: true
        });
    
        return {__html: md.render(recipe.summary)};
    }
      
    function Content() {
        return <div className='desc-source'dangerouslySetInnerHTML={createMarkup()} />;
    }

    return transitions.map(({ item, key, props }) =>  item ?
        <animated.div className='content-container' style={props}>
            <SplashPage/>
        </animated.div>
        :
        <animated.div className='content-container' style={props}>
            <div className='grid-root'>
                <Row>
                    <Col>
                    <div id='title-container'>
                        <h1 id='title-food'className='title-header'>{recipe.title}</h1>
                    </div>
                    <div className='food-img-container'>
                        <img id='food-img' alt='food-name' src={recipe.image}/>
                    </div>
                    {Content()}
                    </Col>
                </Row>
                <Row justify='center' gutter='2'>
                    <Col span={8}>
                        <h2 className='title-header'>Ingredients</h2>
                        <Ingredients items={ingred} useMetric={useMetric}/>
                    </Col>
                    <Col span={8} offset={2}>
                        <h2 className='title-header'>Directions</h2>
                        <Directions instructions={instructions}/>
                    </Col>
                </Row>
            </div>

        </animated.div>
    )
}
