import React, {useState} from 'react'
import 'antd/dist/antd.less';
import './css/Results.css'
import { List, Card } from 'antd';
import {useTransition, animated, config} from 'react-spring'
import { ResultCard } from './ResultCard';

export const Results = (_props) => {
    const {results} = _props
    const transition = useTransition( results, result => result.id, {
        from: { transform: "translate3d(150%, 0,0)", opacity: 0},
        enter: { transform: "translate3d(0%, 0,0)", opacity: 1},
        leave: { transform: "translate3d(-150%, 0,0)", opacity: 0},
        config: (item, state) =>
        state === 'leave' ? { duration: 1000 } : config.gentle,
    })
    
    const list = transition.map(({item, props, key}) => item &&
    <animated.div key={key} style={{props}}>
        <ResultCard result={item} onSelectChange={_props.onSelectChange}/>
    </animated.div>
)

    return (
        <>
            {results &&
                <List
                    grid={{
                    gutter: 16,
                    xs: 1,
                    sm: 2,
                    md: 4,
                    lg: 4,
                    xl: 6,
                    xxl: 3,
                    }}
                    dataSource={list}
                    renderItem={item => (
                    <List.Item>
                        {item}
                    </List.Item>
                    )}
                />
            }
        </>
    )
}
