import React, {useState, useRef} from 'react'
import 'antd/dist/antd.less';
import './css/Results.css'
import { List, Card } from 'antd';
import {useTransition, animated, config} from 'react-spring'
import { ResultCard } from './ResultCard';
import useMeasure from "use-measure";

export const Results = (_props) => {
    const {results} = _props
    const transition = useTransition( results, result => result.id, {
        config: {mass:1, tension:500, friction:18},
        from: { position: "absolute", zIndex: 1, transform: "translate3d(-25%, 0,0)", opacity: 0},
        enter: { transform: "translate3d(0%, 0,0)", opacity: 1},
        leave: { transform: "translate3d(-25%, 0,0)", opacity: 0},
        delay: 500,
    })
    
    const list = transition.map(({item, props, key}) => item &&
    <animated.div key={key} style={{'minHeight': "250px", ...props}}>
        <ResultCard result={item} onSelectChange={_props.onSelectChange}/>
    </animated.div>
    )

    return (
            <List
                grid={{
                gutter: 16,
                xs: 1,
                sm: 2,
                md: 4,
                lg: 4,
                xl: 6,
                xxl: 3,
                }}
                style={{height:"100%", position: "relative"}}
                dataSource={list}
                renderItem={item => (
                <List.Item style={{position: "relative"}}>
                    {item}
                </List.Item>
                )}
            />
    )
}
