import React, {useState, useRef} from 'react'
import 'antd/dist/antd.less';
import './css/Results.css'
import { List, Card } from 'antd';
import {useTransition, animated, config} from 'react-spring'
import { ResultCard } from './ResultCard';
import useMeasure from "use-measure";

export const Results = (_props) => {
    const {results} = _props
    const transition = useTransition( results, result => result.id, {
        config: {mass:1, tension:250, friction:18},
        from: { transform: "translate3d(-50%, 0,0)", opacity: 0},
        enter: { transform: "translate3d(0%, 0,0)", opacity: 1},
        leave: { transform: "translate3d(-25%, 0,0)", opacity: 0},
        delay: 500,
    })
    
    const list = transition.map(({item, props, key}) =>
    <animated.div className="animated-div" key={key} style={{position: "absolute", ...props}}>
        <ResultCard result={item} onSelectChange={_props.onSelectChange}/>
    </animated.div>
    )

    return (
            <List
                grid={{
                gutter: 16,
                column: 3
                }}
                style={{position: "relative"}}
                dataSource={list}
                renderItem={item => (
                    
                <List.Item style={{height: '220px !important', position: "absolute", zIndex: 1}}>
                    <div style={{height: "100%", position: "absolute"}}>
                        {item}
                    </div>
                </List.Item>
                )}
            />
    )
}
