import React from 'react'
import 'antd/dist/antd.less';
import './css/Results.css'
import {useTransition, animated} from 'react-spring'
import { ResultCard } from './ResultCard';

export const Results = (_props) => {

    const transition = useTransition( _props.results, {
        from: { opacity: 0, marginLeft: -100, marginRight: 100},
        enter: { opacity: 1, marginLeft: 0, marginRight: 0},
        leave: { opacity: 0, marginLeft: -100, marginRight: 100}
    })
    const list = transition.map((props, item) => {
        <animated.div style={props}>
            <ResultCard result={item} onSelectChange={_props.onSelectChange}/>
        </animated.div>
    })

    return (
        <>
            {_props.results && list}
        </>
    )
}
